import { useState } from "react";
import ReactDOM from "react-dom";
import { Snowflake } from "react-snowfall";

const initialItems = [
  { id: 1, description: "Passport", quantity: 2, packed: false },
  { id: 2, description: "Socks", quantity: 12, packed: false },
  { id: 3, description: "Charger", quantity: 1, packed: true },
];

export default function App() {
  const [items, setItems] = useState([]);
  const [showLoader, setShowLoader] = useState(true);
  function handleAddItems(item) {
    setItems((items) => [...items, item]);
  }
  function handlePlayClick() {
    setShowLoader(false);
  }
  return (
    <div class="container">
      <div class="row">
        <div class="loader">
          <button id="play" onClick={handlePlayClick}>
            Start
          </button>
        </div>
      </div>
      <canvas id="confetti"></canvas>
    </div>
  );
}

function Logo() {
  return <h1>üéÇHappy Birthdayüéâüë®</h1>;
}
function Form({ onAddItems }) {
  const [description, setDescription] = useState("");
  const [quantity, setQuantity] = useState(1);

  function handleSubmit(e) {
    e.preventDefault();
    if (!description) return;
    const newItem = { description, quantity, packed: false, id: Date.now() };
    setDescription("");
    setQuantity(1);
    onAddItems(newItem);
  }

  return (
    <form className="add-form" onSubmit={handleSubmit}>
      <div>
        <h3>Please add your birthday message along with your name</h3>
      </div>
      <br />
      <div>
        <input
          type="text"
          placeholder="Message..."
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
        <button>Add</button>
      </div>
    </form>
  );
}
function PackingList({ items }) {
  return (
    <div className="list">
      <ul>
        {items.map((item) => (
          <Item item={item} key={item.id} />
        ))}
      </ul>
    </div>
  );
}
function Stats() {
  return (
    <footer className="stats">
      <em>üëúYou have X items on your list and you already packed X (X%)</em>
    </footer>
  );
}

function Item({ item }) {
  return (
    <li>
      <span style={item.packed ? { textDecoration: "line-through" } : {}}>
        {item.description}
      </span>
      <button>‚ùå</button>
    </li>
  );
}
